  const [curNum,setNum] = useState(0)
  return(
    // React Fragment
    <> 
      <div className='main'>
        <div className='centerDiv'>
          <h1>{curNum}</h1>
          <div className='btn'>
            <button>Increase</button>
            <button>Decrease</button>
          </div>
        </div>
      </div>
    </>
  )
}

export default App;

